class Largest{
    constructor(numeros){
        this.numeros =numeros
        this.separar = []
        this.maximo = 1;
        this.veri = 0;
        this.j=1;
    }
    hallarProducto(){
        this.separar = this.numeros.split("");
        for(let i=0; i<=this.separar.length;i++){
            for(let j=i;j<=i+12;j++){
                this.maximo = this.maximo*(parseInt(this.separar[j]))
            }
            if(this.maximo>this.veri){
                this.veri=this.maximo
            }
            this.maximo=1;
            
        }
        return this.veri
    }
}

var digitos = "";

var largo = new Largest(digitos)
console.log(largo.hallarProducto());

